package com.amir.todo.data.mappersimport androidx.compose.ui.graphics.Colorimport com.amir.todo.data.model.CategoryEntityimport com.amir.todo.domain.model.Categoryimport androidx.core.graphics.toColorIntfun CategoryEntity.toDomain(): Category {    return Category(        id = id,        title = title,        color = Color(color.toColorInt())    )}