package com.amir.todo.data.mappersimport amir.tododb.CategoryEntityimport androidx.compose.ui.graphics.Colorimport com.amir.todo.domain.model.Categoryimport androidx.core.graphics.toColorIntfun CategoryEntity.toDomain(): Category {    return Category(        id = id.toInt(),        title = title,        color = Color(color.toColorInt())    )}fun Category.toEntity(): CategoryEntity {    return CategoryEntity(        id = id.toLong(),        title = title,        color = color.value.toString()    )}