package com.amir.todo.data.mappersimport amir.tododb.ReminderEntityimport com.amir.todo.domain.model.Reminderfun ReminderEntity.toDomain():Reminder {    return Reminder(        timeBefore = timeBefore,        isEnabled = isEnabled == 1L    )}fun Reminder.toEntity(taskId :Long): ReminderEntity {    return ReminderEntity(        timeBefore = timeBefore,        isEnabled = if (isEnabled) 1 else 0,        taskId = taskId    )}