package com.amir.todo.data.mappersimport amir.tododb.SubTaskEntityimport com.amir.todo.domain.model.SubTaskfun SubTaskEntity.toDomain(): SubTask {    return SubTask(        title = title,        isDone = isDone == 1L,        taskId = taskId.toInt(),        id = id.toInt()    )}fun SubTask.toEntity(): SubTaskEntity {    return SubTaskEntity(        taskId = taskId.toLong(),        title = title,        isDone = if (isDone) 1 else 0,        id = id.toLong()    )}