package com.amir.todo.data.mappersimport android.os.Buildimport androidx.annotation.RequiresApiimport com.amir.todo.data.model.TaskEntityimport com.amir.todo.domain.model.Taskimport java.time.LocalDate@RequiresApi(Build.VERSION_CODES.O)fun TaskEntity.toDomain(): Task {    return Task(        id = id,        title = title,        isDone = isDone,        description = description,        date = LocalDate.parse(date),        reminder = remainder.toDomain(),        subTask = subTask.map { it.toDomain() },        category = category.toDomain(),        important = important    )}