package com.amir.todo.domain.Repositoryimport com.amir.todo.domain.model.Taskimport com.amir.todo.util.StateManagementimport kotlinx.coroutines.flow.Flowinterface TodoRepository {    suspend fun addTask(task: Task) : Long    suspend fun deleteTask(taskId: Int): Boolean    suspend fun updateTaskCompletion(taskId: Int, isCompleted: Boolean): Boolean    suspend fun editTask(task: Task): Int    suspend fun getTaskById(id: Int): StateManagement<Task>    fun getAllTask(): Flow<List<Task>>}