package com.amir.todo.domain.Repositoryimport com.amir.todo.domain.model.Taskimport kotlinx.coroutines.flow.Flowinterface TodoRepository {    suspend fun addTask(task: Task) : Task    suspend fun deleteTask(taskId: Int) : Boolean    suspend fun updateTaskCompletion(taskId: Int, isCompleted: Boolean) : Boolean    suspend fun editTask(task: Task) : Task    suspend fun getTaskById(id: Int): Task    fun getAllTask(): Flow<List<Task>>}