package com.amir.todo.domain.UseCasesimport com.amir.todo.domain.Repository.TodoRepositoryimport com.amir.todo.util.ErrorTypeimport com.amir.todo.util.Stateimport java.sql.SQLExceptionclass DeleteTask(    val repository: TodoRepository) {    suspend operator fun invoke(taskId: Int): State<Boolean> {        return try {            State.Success(repository.deleteTask(taskId))        } catch (e: SQLException) {            State.Error(                ErrorType.DataBase(message = e.message ?: "DataBase Error")            )        } catch (e: Exception) {            State.Error(                ErrorType.Unknown(message = e.message ?: "Unknown Error")            )        }    }}