package com.amir.todo.domain.useCasesimport com.amir.todo.domain.repository.TodoRepositoryimport com.amir.todo.domain.model.Taskimport com.amir.todo.util.ErrorTypeimport com.amir.todo.util.Stateimport java.sql.SQLExceptionclass GetTaskById(    private val repository: TodoRepository) {    suspend operator fun invoke(id: Int): State<Task?> {        return try {            State.Success(repository.getTaskById(id))        } catch (e: SQLException) {            State.Error(                ErrorType.DataBase(message = e.message ?: "DataBase Error")            )        } catch (e: Exception) {            State.Error(                ErrorType.Unknown(message = e.message ?: "Unknown Error")            )        }    }}