package com.amir.todo.domain.useCasesimport com.amir.todo.domain.repository.TodoRepositoryimport com.amir.todo.util.ErrorTypeimport com.amir.todo.util.Stateimport java.sql.SQLExceptionclass UpdateTaskCompletion(    private val repository: TodoRepository) {    suspend operator fun invoke(taskId: Int, isCompleted: Boolean) : State<Boolean> {        return try {            State.Success(repository.updateTaskCompletion(taskId,isCompleted))        } catch (e : SQLException){            State.Error(                ErrorType.DataBase(message = e.message ?: "DataBase Error")            )        } catch (e : Exception){            State.Error(ErrorType.Unknown(message = e.message ?: "Unknown Error"))        }    }}