package com.amir.todo.utilimport androidx.lifecycle.ViewModelsealed class ErrorType {    abstract val message: String    data class DataBase(override val message: String) : ErrorType()    data class ViewModel(override val message: String) : ErrorType()    data class Unknown(override val message: String) : ErrorType()    override fun toString(): String {        return when (this) {            is DataBase -> "Database Error: $message"            is ViewModel -> "ViewModel Error: $message"            is Unknown -> "Unknown Error: $message"        }    }}